import React,{useState} from 'react'
import "../components/style.css"
import WeatherDetails from './WeatherDetails';

function SearchMain() {
    const [searchTerm, seSearchTerm]=useState('Cancun');

    const getWeatherInfo= async ()=>{
        try{
            let url =`http://api.openweathermap.org/data/2.5/weather?q=${searchTerm}&appid=9bd32898ad03753167ef916bdce83639`
            let res = await fetch(url);
            let data = await res.json();
            console.log(data);
        }
        catch(error){
            console.log(error);
        }
    }



    useEffect(() => {
        getWeatherInfo()

    }, []);

    return (
        <div>
            <div className="wrap">
                <div className="search">
                    <input
                        type="search" 
                        placeholder="Search City"
                        id="search" 
                        value={searchTerm} 
                        onChange={(e)=>seSearchTerm(e.target.value)}/>
                </div>
                <button className="searchButton" onClick={getWeatherInfo}>Search</button>
            </div>
            <WeatherDetails/>
        </div>
    )
}

export default SearchMain
